package types

import (
	"fmt"

	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/common/hexutil"
	"github.com/ethereum/go-ethereum/crypto"
)

// This file contains hardcoded predeploys. As of now, there is no intention
// to support predeploys from any other source than this file.
// Predeploys are different from precompiles in the sense that precompiles
// are just interfaces and run in Go. Predeploys are full contracts that
// run in Solidity. They may be stateful, however, the ones below are not.
// The reason for inclusion of each predeploy is provided as a comment next to
// its definition. Some of these predeploys may be used to deploy others in a
// deterministic manner.

// Predeploy represents a predeployed contract with its address and bytecode.
type Predeploy struct {
	// Address is the address of the predeploy contract.
	Address string
	// Code is the hex-encoded runtime bytecode of the predeploy contract.
	// Do not start it with 0x.
	// Fetch it by using `cast code $ADDR --rpc-url $RPC_URL`.
	Code string
}

// DefaultPredeploys is a list of predeploys that are included at genesis.
var DefaultPredeploys = []Predeploy{
	create2, create2Deployer, createX, safeSingletonFactory, multicall3,
}

// GetByteAddress returns the address as a common.Address.
func (p Predeploy) GetByteAddress() common.Address {
	return common.HexToAddress(p.Address)
}

// GetByteCode returns the bytecode as a byte slice.
func (p Predeploy) GetByteCode() []byte {
	return common.Hex2Bytes(p.Code)
}

// GetCodeHash returns the keccak256 hash of the bytecode.
func (p Predeploy) GetCodeHash() common.Hash {
	return crypto.Keccak256Hash(p.GetByteCode())
}

// Validate the predeploys
func init() {
	for _, predeploy := range DefaultPredeploys {
		// Validate the address
		if !common.IsHexAddress(predeploy.Address) {
			panic(fmt.Sprintf("invalid predeploy address %s", predeploy.Address))
		}
		// Ensure the code is available
		if len(predeploy.Code) == 0 {
			panic(fmt.Sprintf("empty predeploy code for address %s", predeploy.Address))
		}
		// Ensure the code is not prefixed with 0x
		if predeploy.Code[:2] == "0x" {
			panic(fmt.Sprintf("predeploy code for address %s must not start with 0x", predeploy.Address))
		}
		// Ensure the code is valid hex
		if _, err := hexutil.Decode("0x" + predeploy.Code); err != nil {
			panic(fmt.Sprintf("invalid predeploy code for address %s: %v", predeploy.Address, err))
		}
	}
}

var (
	// https://github.com/Arachnid/deterministic-deployment-proxy
	// Include here because it is deployed with a pre-EIP155 tx which is not supported.
	create2 = Predeploy{
		Address: "0x4e59b44847b379578588920cA78FbF26c0B4956C",
		Code:    "7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffe03601600081602082378035828234f58015156039578182fd5b8082525050506014600cf3",
	}
	// https://github.com/pcaversaccio/create2deployer
	// Can be deployed with a request to the dev. But easier to predeploy it.
	create2Deployer = Predeploy{
		Address: "0x13b0D85CcB8bf860b6b79AF3029fCA081AE9beF2",
		Code:    "",
	}
	// https://github.com/pcaversaccio/createx
	// Dev provides a signed pre-EIP155 tx to deploy this contract, which we do not support.
	createX = Predeploy{
		Address: "0xba5Ed099633D3B313e4D5F7bdc1305d3c28ba5Ed",
		Code:    "",
	}
	// https://github.com/safe-global/safe-singleton-factory
	// The Safe team can deploy this contract, if we ask them. But, this is easier + supported.
	// Unknown items can be set to blank values in the corresponding artifact per:
	// https://github.com/safe-global/safe-singleton-factory?tab=readme-ov-file#op-stack
	safeSingletonFactory = Predeploy{
		Address: "0x914d7Fec6aaC8cd542e72Bca78B30650d45643d7",
		Code:    create2.Code,
	}
	// https://github.com/mds1/multicall
	// The key to deploy this is compromised. Therefore, we instead make it a predeploy.
	multicall3 = Predeploy{
		Address: "0xcA11bde05977b3631167028862bE2a173976CA11",
		Code:    "",
	}
)
